package strings_Immutability;

public class string_Literal_Example {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str1 = "Hello";
        String str2 = "Hello";
        
        System.out.println("str1: " + str1);
        
        str1 = "World";

        
        System.out.println("str1: " + str1);
        System.out.println("str2: " + str2);

	}

}
